import { FC } from "react";
import styles from "./TodoItem.module.css";

function TodoItem({
  text,
  onRemoveTodo,
}: {
  text: string;
  onRemoveTodo: (id: string) => void;
}) {
  return (
    <li onClick={() => handleRemoveTodo(id)} className={styles.item}>
      {text}
    </li>
  );
}

export default TodoItem;
