import { FC } from "react";
import styles from "./TodoItem.module.css";

function TodoItem({
  text,
  onRemoveTodo,
}: {
  text: string;
  onRemoveTodo: (text: string) => {};
}) {
  function handleRemoveTodo() {
    onRemoveTodo(text);
  }
  return (
    <li onClick={handleRemoveTodo} className={styles.item}>
      {text}
    </li>
  );
}

export default TodoItem;
